def get_pixel_size(obstacles, grid_size=300):
    all_x = [obs['x_min'] for obs in obstacles] + [obs['x_max'] for obs in obstacles]
    all_z = [obs['z_min'] for obs in obstacles] + [obs['z_max'] for obs in obstacles]

    x_range = max(all_x) - min(all_x)
    z_range = max(all_z) - min(all_z)

    pixel_size_x = x_range / grid_size
    pixel_size_z = z_range / grid_size

    print(f"Pixel size X (m/pixel): {pixel_size_x}")
    print(f"Pixel size Z (m/pixel): {pixel_size_z}")
    return pixel_size_x, pixel_size_z

